INSERT INTO `configs` VALUES (3, 1, 'our-story', '{\"id\":\"Tentang Kami\",\"en\":\"about us\",\"fr\":\"About Us\",\"it\":\"about us\",\"jp\":\"about us\"}', '{\"id\":\"<p class=\\\"Body\\\" style=\\\"text-align: center; \\\"><span lang=\\\"IT\\\">Kuta emergency center merupakan pusat darurat&nbsp; specialist di tengah kuta yang barada di\\ntengah-tengah surga lombok di indonesia. Didirikan pada tauhun 2020 . Kuta\\nemergency menyediakan pelayanan yg komprehensif yang dilakukan oleh tim medikal\\nspesialis yang terlatih.<o:p><\\/o:p><\\/span><\\/p>\\n\\n<p class=\\\"Body\\\" style=\\\"text-align: center; \\\"><span lang=\\\"IT\\\">Pasien mendapatkan pelayanan terbaik di lombok\\ndengan teknologi medikal yang modern. Visi dari kuta emergency adalah\\nmenyediakan kualitas dan skala&nbsp;\\ninternasional serta belas kasih untuk merespon transformasi sosial yang\\ndinamik di indonesia. Kuta emergency adalah satu satunya fasilitas emergency yg\\nlengkap di kuta dengan disertai departmen radiologi<o:p><\\/o:p><\\/span><\\/p>\",\"en\":\"<p class=\\\"Body\\\" style=\\\"text-align: center; \\\"><span lang=\\\"IT\\\">Kuta Emergency is a highly specialised private\\nemergency center in the hearth of Kuta in the middle of the Indonesian paradise\\nof Lombok in Indonesia.<\\/span><\\/p><p class=\\\"Body\\\" style=\\\"text-align: center; \\\">&lt;dokter&gt;halo pak budi ini isinya&lt;\\/dokter&gt;<\\/p><p class=\\\"Body\\\" style=\\\"text-align: center; \\\"><span lang=\\\"IT\\\"><o:p><\\/o:p><\\/span><\\/p>\\n\\n<p class=\\\"Body\\\" style=\\\"text-align: center; \\\"><span lang=\\\"IT\\\">Established in 2020, Kuta Emergency <\\/span><span lang=\\\"EN-US\\\">provides a multitude of\\ncomprehensive services performed by a specially trained medical tea<\\/span><span lang=\\\"IT\\\">m.<o:p><\\/o:p><\\/span><\\/p>\\n\\n<p class=\\\"Body\\\" style=\\\"text-align: center;\\\"><span lang=\\\"IT\\\">Patients can be assured that they are receiving the\\nbest medical care in Lombok as we are fully equipped with ultra-modern medical\\ntechnology.<o:p><\\/o:p><\\/span><\\/p>\\n\\n<p class=\\\"Body\\\" style=\\\"text-align: center;\\\"><span lang=\\\"IT\\\">The company\\u2019s vision of International quality,\\nscale, and compassion establishes the platform for Kuta Emergency to respond to\\nthe dynamic social transformation in Indonesia.<o:p><\\/o:p><\\/span><\\/p>\\n\\n<p class=\\\"Body\\\" style=\\\"text-align: center;\\\"><span lang=\\\"IT\\\">Kuta Emergency is the only emergency facility fully\\nequipped in Kuta with a radiology department.&nbsp;<o:p><\\/o:p><\\/span><\\/p>\",\"fr\":\"<p class=\\\"Body\\\"><span lang=\\\"EN-US\\\">Kuta Emergency <\\/span><span lang=\\\"FR\\\">\\u00e9 <\\/span><span lang=\\\"PT\\\">um centro m<\\/span><span lang=\\\"FR\\\">\\u00e9<\\/span><span lang=\\\"PT\\\">dico de pronto socorro altamente\\nespecializado em de Kuta, no centro da paradis<\\/span><span lang=\\\"IT\\\">\\u00ed<\\/span><span lang=\\\"PT\\\">aca ilha de Lombok, em Idonesia. Aberta\\ndesde 2020, Kuta Emergency oferece uma diversidade de servi<\\/span><span lang=\\\"IT\\\">\\u00e7<\\/span><span lang=\\\"ES-TRAD\\\">os m<\\/span><span lang=\\\"FR\\\">\\u00e9<\\/span><span lang=\\\"PT\\\">dicos de urg<\\/span><span lang=\\\"IT\\\">\\u00ea<\\/span><span lang=\\\"PT\\\">ncia e emerg<\\/span><span lang=\\\"IT\\\">\\u00ea<\\/span><span lang=\\\"ES-TRAD\\\">ncia por m<\\/span><span lang=\\\"FR\\\">\\u00e9<\\/span><span lang=\\\"PT\\\">rito do nosso time multidisciplinar e altamente qualificado. <\\/span><span lang=\\\"IT\\\"><o:p><\\/o:p><\\/span><\\/p>\\n\\n<p class=\\\"Body\\\"><span lang=\\\"PT\\\">Os nossos pacientes\\nrecebem o melhor tratamento m<\\/span><span lang=\\\"FR\\\">\\u00e9<\\/span><span lang=\\\"PT\\\">dico da ilha, gra<\\/span><span lang=\\\"IT\\\">\\u00e7<\\/span><span lang=\\\"PT\\\">as aos nossos equipamentos\\nm<\\/span><span lang=\\\"FR\\\">\\u00e9<\\/span><span lang=\\\"PT\\\">dicos modernos e tecnol<\\/span><span lang=\\\"ES-TRAD\\\">\\u00f3<\\/span><span lang=\\\"PT\\\">gicos. <\\/span><span lang=\\\"IT\\\"><o:p><\\/o:p><\\/span><\\/p>\\n\\n<p class=\\\"Body\\\"><span lang=\\\"PT\\\">A nossa estrutura\\nrespeita os padr\\u00f5es internacionais de qualidade para atender da melhor forma\\nposs<\\/span><span lang=\\\"IT\\\">\\u00ed<\\/span><span lang=\\\"PT\\\">vel\\nas exig<\\/span><span lang=\\\"IT\\\">\\u00ea<\\/span><span lang=\\\"PT\\\">ncias m<\\/span><span lang=\\\"FR\\\">\\u00e9<\\/span><span lang=\\\"PT\\\">dicas dos turistas que visitam a ilha. <\\/span><span lang=\\\"IT\\\"><o:p><\\/o:p><\\/span><\\/p>\\n\\n<p class=\\\"Body\\\"><span lang=\\\"EN-US\\\">Kuta Emergency <\\/span><span lang=\\\"FR\\\">\\u00e9 <\\/span><span lang=\\\"IT\\\">a \\u00fa<\\/span><span lang=\\\"PT\\\">nica estrutura de Kuta completamente\\nequipada com um departamento de radiologia.&nbsp;<\\/span><span lang=\\\"IT\\\"><o:p><\\/o:p><\\/span><\\/p>\",\"it\":\"<p class=\\\"Body\\\"><span lang=\\\"IT\\\">Kuta Emergency \\u00e8 un centro medico privato di pronto\\nsoccorso altamente specializzato nel cuore di Kuta al centro della paradisiaca\\nisola di Lombok in Indonesia.<o:p><\\/o:p><\\/span><\\/p><p class=\\\"Body\\\"><span lang=\\\"IT\\\">Aperta dal 2020, Kuta Emergency offre una\\nmoltitudine di servizi medici di urgenza ed emergenza per merito del nostro\\nteam multidisciplinare ed altamente specializzato.<o:p><\\/o:p><\\/span><\\/p><p class=\\\"Body\\\"><span lang=\\\"IT\\\">I nostri pazienti ricevono il migliore trattamento\\nmedico possibile sull\\u2019isola grazie alle nostre attrezzature mediche moderne e\\ntecnologiche. <o:p><\\/o:p><\\/span><\\/p><p class=\\\"Body\\\"><span lang=\\\"IT\\\">La nostra struttura rispetta gli standard\\ninternazionali di qualit\\u00e0 per rispondere al meglio alle esigenze mediche dei\\nturisti che visitano l\\u2019isola.&nbsp; <o:p><\\/o:p><\\/span><\\/p><p>\\n\\n\\n\\n\\n\\n\\n\\n<\\/p><p class=\\\"Body\\\"><span lang=\\\"IT\\\">Kuta Emergency \\u00e8 la sola struttura di